import random

class QuestionGenerator:
    def __init__(self):
        pass

    def generate_mcqs(self, topic, count=5):
        questions = []
        for i in range(count):
            question = {
                "question": f"What is {topic} concept #{i+1}?",
                "options": [
                    f"{topic} Option A",
                    f"{topic} Option B",
                    f"{topic} Option C",
                    f"{topic} Option D"
                ],
                "answer": f"{topic} Option A"
            }
            questions.append(question)
        return questions
